import './App.css';
import React, { useState } from 'react';

function App() {

  const [grid, setGrid] = useState (
    ['','','','','','','','','']
  )
  const [turn ,setTurn ]= useState('X') 



  const chooseSquare =square => {


    if (grid[square]==='O'||grid[square]==='X') {
      alert ('veuillez cliquez autre part') 
      return
    }
     const newGrid = grid.slice()
     newGrid[square]= turn ; 
     setGrid (newGrid);
      if (turn === 'X'){
         setTurn ('O')
      }
      else if (turn === 'O') setTurn('X')  

    if ((Ngrid[0]==='O'&&Ngrid[1]==='O'&&Ngrid[2]==='O')||
        (Ngrid[3]==='O'&&Ngrid[4]==='O'&&Ngrid[5]==='O')|| 
        (Ngrid[6]==='O'&&Ngrid[7]==='O'&&Ngrid[8]==='O')||
        (Ngrid[0]==='O'&&Ngrid[3]==='O'&&Ngrid[6]==='O')||
        (Ngrid[1]==='O'&&Ngrid[4]==='O'&&Ngrid[7]==='O')||
        (Ngrid[2]==='O'&&Ngrid[5]==='O'&&Ngrid[8]==='O')||
        (Ngrid[0]==='O'&&Ngrid[4]==='O'&&Ngrid[9]==='O')||
        (Ngrid[2]==='O'&&Ngrid[4]==='O'&&Ngrid[6]==='O')
    ){
      alert ('Le joueur O a gagner')
      setNGrid(['','','','','','','','',''])
      } 
      else if (
      (Ngrid[0]==='X'&&Ngrid[1]==='X'&&Ngrid[2]==='X')||
      (Ngrid[3]==='X'&&Ngrid[4]==='X'&&Ngrid[5]==='X')||
      (Ngrid[6]==='X'&&Ngrid[7]==='X'&&Ngrid[8]==='X')||
      (Ngrid[0]==='X'&&Ngrid[3]==='X'&&Ngrid[6]==='X')||
      (Ngrid[1]==='X'&&Ngrid[4]==='X'&&Ngrid[7]==='X')||
      (Ngrid[2]==='X'&&Ngrid[5]==='X'&&Ngrid[8]==='X')||
      (Ngrid[0]==='X'&&Ngrid[4]==='X'&&Ngrid[8]==='X')||
      (Ngrid[2]==='X'&&Ngrid[4]==='X'&&Ngrid[6]==='X')
      )
      {
      alert ('Le joueur X a gagner')
      setGrid(['','','','','','','','',''])
      }
    }

  const page = (
    <React.Fragment>
      <h1 className='title'>MORPION</h1>
      <div className="container">
      {/*1ere ligne*/}
        <div className="block" onClick={() =>chooseSquare(0)}> {grid[0]}</div>
        <div className="block" onClick={() =>chooseSquare(1)}> {grid[1]}</div>
        <div className="block" onClick={() =>chooseSquare(2)}> {grid[2]}</div>
      </div>
      {/*2eme ligne*/}
      <div className="container">
        <div className="block" onClick={() =>chooseSquare(3)}> {grid[3]} </div>
        <div className="block" onClick={() =>chooseSquare(4)}> {grid[4]} </div>
        <div className="block" onClick={() =>chooseSquare(5)}> {grid[5]} </div>
      </div>
      {/*3eme ligne*/}
      <div className="container">
        <div className="block" onClick={() =>chooseSquare(6)}> {grid[6]} </div>
        <div className="block" onClick={() =>chooseSquare(7)}> {grid[7]} </div>
        <div className="block" onClick={() =>chooseSquare(8)}> {grid[8]} </div>
      </div>
    </React.Fragment>


  )
  return page

}

export default App;
