import './App.css';
import React, { useEffect, useState } from 'react';
import {io} from 'socket.io-client'

function App() {
    const [socket,setSocket] = useState(false)

    useEffect(() => {

        setSocket(socket)
        socket.emit('message du client')
        return () => socket.disconnect()
    })
    return (
    <div className="App">
        <button onClick={() => socket.emit("bouton cliqué")}>clique moi</button>
    </div>
)}

export default App;
