            <div className="block" onClick={() =>chooseSquare(2)}> {grid[2]}</div>
        </div>

        </div>
    )
}
//const loading = ({socket, waiting})


const PreGame = ({socket, waiting, logout}) => {
    return (
        <div className="App">
            <h1 className='title'> Jeux du morpion </h1>
            <div className="container">
                <button className="button"onClick={() => socket.emit("join game")}>join the game</button>
            </div>
            <div className='title'>{waiting}</div>
            <div className="containerLogout">
                <button className="lbutton"onClick={() =>  socket.emit("logout")}>{logout}</button>
            </div>       
        </div>
    )
}

const Logout = ({waiting, logout})

function App() {
    const [socket, setSocket] = useState(false)   
    const [waiting, setWaiting] = useState(false)  
    const [logout, setLogout] = useState(false)
    const [gameStarted, setGameStarted] = useState(false)

    useEffect(() => {
        const socket = io('http://localhost:8080')
        socket.on("waiting for player.....", () => {
            setWaiting('waiting for player............')
            setLogout('logout')
        })
        socket.on("game started",() => {
            setGameStarted(true)
        })
        setSocket(socket)
        return () => socket.disconnect()
    }, [])

    if (gameStarted)
        return <Grid/>
    else 
        return <PreGame
                    socket={socket}
                   // waiting={waiting}
                    //logout={logout}
                />
    /*else 
        return <Loading
                waiting={waiting}
                logout={logout}
                /> */
}

export default App;
